import { useState } from "react";
import Button from "./Button";
import Card from "./Card";

/*
Howto do this error... App.js, onAddUser, return this component if one of such errors exist.
Here, create a box that shades other areas, and explains the error, onClick on nonBox, or the 'ok' button, the box  dissapears due to state
*/

const ErrorModal = (props) => {
  const output = () => {};
  return (
    <Card>
      <header>
        <h2>{props.errTitle}</h2>
      </header>
      <div>
        <p>{props.errWarning}</p>
      </div>
      <footer>
        <Button onClick={onOkayHandler}>Okay</Button>
      </footer>
    </Card>
  );
};
