import { useState } from "react";
import styles from "./UserInput.module.css";

const UserInput = (props) => {
  const [enteredTextValue, setEnteredTextValue] = useState("");
  const [enteredNumValue, setEnteredNumValue] = useState("");
  const [isTextValid, setIsTextValid] = useState(true);
  const [isValidNum, setIsValidNum] = useState(null);

  const userInputTextHandler = (event) => {
    if (event.target.value.trim().length > 0) {
      setIsTextValid(true);
    }
    setEnteredTextValue(event.target.value);
  };

  const userInputNumHandler = (event) => {
    if (event.target.value.trim().length > 0) {
      setIsValidNum(true);
    }
    setEnteredNumValue(event.target.value);
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();
    if (enteredValue.trim().length === 0) {
      setIsValid(false);
      return;
    }
    props.onAddUser(enteredValue);
  };

  return (
    <form onSubmit={formSubmitHandler}>
      <div
        className={`${styles["form-control"]} ${
          !isTextValid || (!isValidNum && styles.invalid)
        }`}
      >
        <label>Username</label>
        <input type="text" onChange={userInputTextHandler} />
        <label>Age</label>
        <input type="number" onChange={userInputNumHandler} />
      </div>
      <button type="submit">Add User</button>
    </form>
  );
};

export default UserInput;
