import { useState } from "react";
import UserList from "./Users/UserList";
import UserInput from "./UserForm/Input/UserInput";
import "./App.css";

function App() {
  const [users, setUsers] = useState([]);

  const addUserHandler = (enteredText) => {
    setUsers((prevUsers) => {
      const updatedUsers = [...prevUsers];
      updatedUsers.unshift({ text: enteredText, id: Math.random().toString() });
      return updatedUsers;
    });
  };

  const deleteUserHandler = (userId) => {
    setUsers((prevUsers) => {
      const updatedUsers = prevUsers.filter((user) => user.id !== userId);
      return updatedUsers;
    });
  };

  let content = <p style={{ textAlign: "center" }}>No Users Found.</p>;

  if (users.length > 0) {
    content = <UserList items={users} onDeleteItem={deleteUserHandler} />;
  }

  return (
    <div>
      <section id="goal-form">
        <UserInput onAddUser={addUserHandler} />
      </section>
      <section id="goals">{content}</section>
    </div>
  );
}

export default App;
